// Improved navigation function with smooth transitions
function navigateToHome() {
    const mainContent = document.getElementById('main-content');

    // Add fade out animation
    mainContent.style.animation = 'pageTransitionOut 0.3s ease-in-out';

    // Wait for animation to complete before rendering
    setTimeout(() => {
        appState.currentPage = 'home';
        renderHomePage();
        mainContent.style.animation = 'pageTransitionIn 0.4s ease-in-out';
    }, 300);
}

// Navigate to movie detail with smooth transition
function navigateToMovie(movieId) {
    const mainContent = document.getElementById('main-content');
    mainContent.style.animation = 'pageTransitionOut 0.3s ease-in-out';

    setTimeout(() => {
        appState.currentPage = 'movie-detail';
        renderMovieDetail(movieId);
        mainContent.style.animation = 'pageTransitionIn 0.4s ease-in-out';
    }, 300);
}

// Navigate to anime detail with smooth transition
function navigateToAnime(animeId) {
    const mainContent = document.getElementById('main-content');
    mainContent.style.animation = 'pageTransitionOut 0.3s ease-in-out';

    setTimeout(() => {
        appState.currentPage = 'anime-detail';
        renderAnimeDetail(animeId);
        mainContent.style.animation = 'pageTransitionIn 0.4s ease-in-out';
    }, 300);
}

// EVENT HANDLERS
// ============================================
function addCardEventListeners() {
    // Add click event to movie cards
    const movieCards = document.querySelectorAll('.card[data-type="movie"]');
    movieCards.forEach(card => {
        card.addEventListener('click', () => {
            const movieId = card.dataset.id;
            navigateToMovie(movieId);
        });

        // Add keyboard support for card navigation (Enter key)
        card.setAttribute('tabindex', '0');
        card.setAttribute('role', 'button');
        card.addEventListener('keydown', (e) => {
            if (e.key === 'Enter' || e.key === ' ') {
                e.preventDefault();
                const movieId = card.dataset.id;
                navigateToMovie(movieId);
            }
        });
    });

    // Add click event to anime cards
    const animeCards = document.querySelectorAll('.card[data-type="anime"]');
    animeCards.forEach(card => {
        card.addEventListener('click', () => {
            const animeId = card.dataset.id;
            navigateToAnime(animeId);
        });

        // Add keyboard support for card navigation (Enter key)
        card.setAttribute('tabindex', '0');
        card.setAttribute('role', 'button');
        card.addEventListener('keydown', (e) => {
            if (e.key === 'Enter' || e.key === ' ') {
                e.preventDefault();
                const animeId = card.dataset.id;
                navigateToAnime(animeId);
            }
        });
    });
}

// Search Functionality
function handleSearch() {
    const searchQuery = document.getElementById('searchInput').value.toLowerCase().trim();
    appState.searchQuery = searchQuery;

    // Filter movies
    if (searchQuery === '') {
        appState.filteredMovies = [...data.movies];
        appState.filteredAnime = [...data.anime];
    } else {
        appState.filteredMovies = data.movies.filter(movie =>
            movie.title.toLowerCase().includes(searchQuery) ||
            movie.year.toString().includes(searchQuery)
        );

        appState.filteredAnime = data.anime.filter(anime =>
            anime.title.toLowerCase().includes(searchQuery) ||
            anime.year.toString().includes(searchQuery)
        );
    }

    // Re-render content based on current page
    if (appState.currentPage === 'home') {
        renderMovieCards();
        renderAnimeCards();

        // Re-add event listeners
        setTimeout(() => {
            addCardEventListeners();
        }, 100);
    }
}

// ============================================
// INITIALIZATION
// ============================================
document.addEventListener('DOMContentLoaded', () => {
    // Initial render
    renderHomePage();

    // Set up search with debounce
    const searchInput = document.getElementById('searchInput');
    const debouncedSearch = debounce(handleSearch, 300);
    searchInput.addEventListener('input', debouncedSearch);

    // Focus on search input
    searchInput.focus();

    // Update page title based on current view
    updatePageTitle();
});

// Update page title based on current view
function updatePageTitle() {
    let title = 'WatchRoute - Discover Movies & Anime';

    if (appState.currentPage === 'movie-detail' && appState.currentMovieId) {
        const movie = data.movies.find(m => m.id === parseInt(appState.currentMovieId));
        if (movie) title = `${movie.title} - WatchRoute`;
    } else if (appState.currentPage === 'anime-detail' && appState.currentAnimeId) {
        const anime = data.anime.find(a => a.id === parseInt(appState.currentAnimeId));
        if (anime) title = `${anime.title} - WatchRoute`;
    }

    document.title = title;
}

